
public class Ejercicio1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=25, b=5;
		int suma=a+b, resta = a-b, multiplicacion=a*b, division=a/b, resto=a%b;
		System.out.println("- La suma de a y b es igual a " + suma);
		System.out.println("- a menos b es igual a " + resta);
		System.out.println("- La multiplicación de a por b es igual a " + multiplicacion);
		System.out.println("- a dividio b es igual a " + division);
		System.out.println("- El resto de la división de a por b es igual a  " + resto);
	}

}
